ProductGalleryQuery:
query ProductGalleryQuery($first: Int!, $after: String!, $sort: StoreSort!, $term: String!, $selectedFacets: [IStoreSelectedFacet!]!) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    products {
      pageInfo {
        totalCount
      }
    }
    facets {
      key
      label
      type
      values {
        label
        value
        selected
        quantity
      }
    }
  }
}

ServerCollectionPageQuery:
query ServerCollectionPageQuery($slug: String!) {
  collection(slug: $slug) {
    seo {
      title
      description
    }
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    meta {
      selectedFacets {
        key
        value
      }
    }
  }
}

ServerProductPageQuery:
query ServerProductPageQuery($id: String!) {
  product(locator: [{key: "id", value: $id}]) {
    id: productID
    slug
    seo {
      title
      description
    }
    brand {
      name
    }
    sku
    gtin
    name
    description
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    image {
      url
      alternateName
    }
    offers {
      lowPrice
      highPrice
      priceCurrency
      offers {
        availability
        price
        priceValidUntil
        priceCurrency
        itemCondition
        seller {
          identifier
        }
        listPrice
      }
    }
    isVariantOf {
      productGroupID
      name
    }
  }
}

ValidateCartMutation:
mutation ValidateCartMutation($cart: IStoreCart!) {
  validateCart(cart: $cart) {
    order {
      orderNumber
      acceptedOffer {
        seller {
          identifier
        }
        quantity
        price
        listPrice
        itemOffered {
          sku
          name
          image {
            url
            alternateName
          }
          brand {
            name
          }
          isVariantOf {
            productGroupID
            name
          }
          gtin
        }
      }
    }
    messages {
      text
      status
    }
  }
}

PersonQuery:
query PersonQuery {
  person {
    id
    email
    givenName
    familyName
  }
}

BrowserProductQuery:
query BrowserProductQuery($locator: [IStoreSelectedFacet!]!) {
  product(locator: $locator) {
    id: productID
    sku
    name
    gtin
    description
    isVariantOf {
      productGroupID
      name
    }
    image {
      url
      alternateName
    }
    brand {
      name
    }
    offers {
      lowPrice
      offers {
        availability
        price
        listPrice
        seller {
          identifier
        }
      }
    }
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
  }
}

ProductsQuery:
query ProductsQuery($first: Int!, $after: String, $sort: StoreSort!, $term: String!, $selectedFacets: [IStoreSelectedFacet!]!) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    products {
      pageInfo {
        totalCount
      }
      edges {
        node {
          id: productID
          slug
          sku
          brand {
            brandName: name
            name
          }
          name
          gtin
          isVariantOf {
            productGroupID
            name
          }
          image {
            url
            alternateName
          }
          offers {
            lowPrice
            offers {
              availability
              price
              listPrice
              quantity
              seller {
                identifier
              }
            }
          }
        }
      }
    }
  }
}
