ProductGalleryQuery:
query ProductGalleryQuery($first: Int!, $after: String!, $sort: StoreSort!, $term: String!, $selectedFacets: [IStoreSelectedFacet!]!) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    products {
      pageInfo {
        totalCount
      }
    }
    facets {
      ... on StoreFacetRange {
        key
        label
        min {
          selected
          absolute
        }
        max {
          selected
          absolute
        }
        __typename
      }
      ... on StoreFacetBoolean {
        key
        label
        values {
          label
          value
          selected
          quantity
        }
        __typename
      }
    }
  }
}

ServerCollectionPageQuery:
query ServerCollectionPageQuery($slug: String!) {
  collection(slug: $slug) {
    seo {
      title
      description
    }
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    meta {
      selectedFacets {
        key
        value
      }
    }
  }
}

ServerProductPageQuery:
query ServerProductPageQuery($slug: String!) {
  product(locator: [{key: "slug", value: $slug}]) {
    id: productID
    seo {
      title
      description
      canonical
    }
    brand {
      name
    }
    sku
    gtin
    name
    description
    releaseDate
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    image {
      url
      alternateName
    }
    offers {
      lowPrice
      highPrice
      priceCurrency
      offers {
        availability
        price
        priceValidUntil
        priceCurrency
        itemCondition
        seller {
          identifier
        }
        listPrice
      }
    }
    isVariantOf {
      productGroupID
      name
      skuVariants {
        activeVariations
        slugsMap(dominantVariantName: "Color")
        availableVariations(dominantVariantName: "Color")
      }
    }
    additionalProperty {
      propertyID
      name
      value
      valueReference
    }
  }
}

ValidateCartMutation:
mutation ValidateCartMutation($cart: IStoreCart!, $session: IStoreSession!) {
  validateCart(cart: $cart, session: $session) {
    order {
      orderNumber
      acceptedOffer {
        seller {
          identifier
        }
        quantity
        price
        listPrice
        itemOffered {
          sku
          name
          image {
            url
            alternateName
          }
          brand {
            name
          }
          isVariantOf {
            productGroupID
            name
          }
          gtin
          additionalProperty {
            propertyID
            name
            value
            valueReference
          }
        }
      }
    }
    messages {
      text
      status
    }
  }
}

