ProductsQuery:
query ProductsQuery($first: Int!, $after: String, $sort: StoreSort!, $term: String!, $selectedFacets: [IStoreSelectedFacet!]!) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    products {
      pageInfo {
        totalCount
      }
      edges {
        node {
          id: productID
          slug
          sku
          brand {
            brandName: name
            name
          }
          name
          gtin
          isVariantOf {
            productGroupID
            name
          }
          image {
            url
            alternateName
          }
          offers {
            lowPrice
            offers {
              availability
              price
              listPrice
              quantity
              seller {
                identifier
              }
            }
          }
        }
      }
    }
  }
}

SearchSuggestionsQuery:
query SearchSuggestionsQuery($term: String!, $selectedFacets: [IStoreSelectedFacet!]) {
