}

TopSearchSuggestionsQuery:
query TopSearchSuggestionsQuery($term: String!, $selectedFacets: [IStoreSelectedFacet!]) {
  search(first: 5, term: $term, selectedFacets: $selectedFacets) {
    suggestions {
      terms {
        value
      }
    }
  }
}

ValidateSession:
mutation ValidateSession($session: IStoreSession!, $search: String!) {
  validateSession(session: $session, search: $search) {
    locale
    channel
    country
    postalCode
    currency {
      code
      symbol
    }
    person {
      id
      email
      givenName
      familyName
    }
  }
}
